void PWM_Timer_Init(void)
{
  /* FTM1_SC: TOF=0,TOIE=0,CPWMS=0,CLKS=0,PS=0 */
  setReg8(FTM1_SC, 0x00U);             /* Stop HW; disable overflow interrupt and set prescaler to 0 */ 
  /* FTM1_C0SC: CHF=0,CHIE=1,MSB=0,MSA=1,ELSB=0,ELSA=0,??=0,??=0 */
  setReg8(FTM1_C0SC, 0x50U);           /* Set output compare mode and enable compare interrupt */ 
  PWM_Timer_SetCV(0x027FU);            /* Initialize appropriate value to the compare/modulo/reload register */
  /* FTM1_CNTH: COUNT_H=0 */
  setReg8(FTM1_CNTH, 0x00U);           /* Reset HW Counter */ 
  /* FTM1_SC: TOF=0,TOIE=0,CPWMS=0,CLKS=1,PS=0 */
  setReg8(FTM1_SC, 0x08U);             /* Set prescaler and run counter */ 
}


