ISR(PWM_Timer_Interrupt)
{
  /* FTM1_C0SC: CHF=0 */
  clrReg8Bits(FTM1_C0SC, 0x80U);       /* Reset compare interrupt request flag */ 
  PWM_Timer_OnInterrupt();             /* Invoke user event */
  IPC_SC_PULIPM = 1U;                  /* Restore Interrupt Priority Mask */
}



